#[function_component]
fn Header() -> Html {
    html! {
        <div class={classes!("header")}>
            <div>
                <em class={classes!("logo-text")}>{"(flint)"}</em>
                <sup><sup><sup>
                    <a href="_" target="_blank">{"(paper)"}</a>
                </sup></sup></sup>
            </div>
        </div>
    }
}

#[function_component]
fn Stuff() -> Html {
    html! {
        <div class={classes!("stuff")}>
            <div class={classes!("stuff-item")}>
                <p class={classes!("stuff-item_left")}>
                    <h2>{"verifiable tamper evidence."}</h2>
                    <p><em>{"flint"}</em>{" ensures the authenticity and integrity of your data by detecting unauthorized changes. Ideal for secure financial transactions, legal documents and government records."}</p>
                    <a href="https://github.com/nairvarun/flint" target="_blank" class={classes!("stuff-github")}>{"github ↗"}</a>
                </p>
            </div>
            <div class={classes!("stuff-item")}>
                <Tree/>
            </div>
        </div>
    }
}

#[function_component]
fn App() -> Html {
    html! {
        <>
            <Header/>
            <Stuff/>
        </>
    }
}

fn main() {
    yew::Renderer::<App>::new().render();
}
